#%%import pandas as pdimport pandas_datareader as webimport matplotlib.pyplot as pltplt.style.use("dark_background")import numpy as npimport datetime #%%start = datetime.date(2010, 1, 1)end = datetime.datetime.today()data = web.DataReader("^IXIC", "yahoo", start, end) #%%close = data[['Adj Close']]roll_1 = data[['Adj Close']].rolling(window=40).mean()roll_2 = data[['Adj Close']].rolling(window=200).mean()plt.figure(figsize = (13, 6))plt.grid(True, color = 'pink')plt.plot(close, 'purple')plt.plot(roll_1, 'c')plt.plot(roll_2, 'r')plt.title("Historical Data")plt.show()#%%data[['Adj Close', 'roll_1', 'roll_2']].plot()